import { useState, useEffect, useCallback } from 'react';

function useFetchMovies(url, transformData = data => data, isObject = false) {
  const [data, setData] = useState(isObject ? {} : []);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // useCallback asegura que transformData no cambie en cada renderizado.
  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      const result = await response.json();
      setData(transformData(result));
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [url, transformData]);

  useEffect(() => {
    fetchData();  // Llama a fetchData cuando cambie el `url`
  }, [url, fetchData]);  // Añade fetchData como dependencia para evitar cambios constantes

  return { data, loading, error };
}

export default useFetchMovies;
