import { useState, useEffect, useCallback } from 'react';

function useFetchMovies(url, transformData = data => data, isObject = false) {
  const [data, setData] = useState(isObject ? {} : []);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Verificar URL y asegurarse de que no esté vacía
  useEffect(() => {
    if (!url) {
      console.log('URL is empty');
      setError('Invalid URL');
      setLoading(false);
    }
  }, [url]);

  // Función de búsqueda con manejo de errores
  const fetchData = useCallback(async () => {
    try {
      console.log('Fetching data from:', url);  // Verifica qué URL se está usando
      setLoading(true);
      setError(null);

      const response = await fetch(url);

      // Verifica si la respuesta es exitosa
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }

      const result = await response.json();
      console.log('Fetched result:', result);  // Verifica la respuesta

      setData(transformData(result));
    } catch (err) {
      console.error('Fetch error:', err.message);  // Verifica el error
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [url, transformData]);

  useEffect(() => {
    if (url) {
      fetchData();
    }
  }, [url, fetchData]);

  return { data, loading, error };
}

export default useFetchMovies;
