import React from 'react';
import { useParams } from 'react-router-dom';
import useFetchMovies from '../hooks/fetchMovies';
import { BASE_URL, MOVIE_DETAIL_ENDPOINT } from '../Config';
import '../css/MovieDetail.css';

function MovieDetail() {
  const { id } = useParams();
  const movieUrl = `${BASE_URL}${MOVIE_DETAIL_ENDPOINT}/${id}`;
  const { data: movieDetails, loading } = useFetchMovies(movieUrl);

  if (loading) return <p>Loading...</p>;
  if (!movieDetails) return <p>Movie not found</p>;

  const movie = movieDetails;

  return (
    <div className="movie-detail">
      <img src={movie.poster} alt={movie.title} className="movie-detail__image" />
      <div className="movie-detail__info">
        <h1 className="movie-detail__title">{movie.title}</h1>
        <p className="movie-detail__text"><strong>Year:</strong> {movie.year}</p>
        <p className="movie-detail__text"><strong>Director:</strong> {movie.director}</p>
        <p className="movie-detail__text"><strong>Genre:</strong> {movie.genre.join(', ')}</p>
        <p className="movie-detail__text"><strong>Duration:</strong> {movie.duration} minutes</p>
        <p className="movie-detail__text"><strong>Language:</strong> {movie.language}</p>
        <p className="movie-detail__text"><strong>Synopsis:</strong> {movie.synopsis}</p>
        <p className="movie-detail__text"><strong>Description:</strong> {movie.description}</p>
        <h3 className="movie-detail__subtitle">Reviews:</h3>
        <ul className="movie-detail__reviews">
          {movie.reviews && movie.reviews.map((review, index) => (
            <li key={index} className="movie-detail__review"><strong>{review.author}</strong>: {review.comment} ({review.rating}/10)</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default MovieDetail;
